import React, { ReactElement } from 'react';
import { Solution } from '../../../lib/types/solution';
import WithoutTaskDataComponent from './solution-item/without-task-data.component';
import WithoutUserDataComponent from './solution-item/without-user-data.component';

export default function SolutionsComponent({
  solutions,
  type,
}: SolutionsProps): ReactElement | null {
  if (!solutions.length) {
    return null;
  }

  const SolutionItem = ({ solution }: { solution: Solution }): ReactElement => {
    if (type === SolutionType.WithoutTaskData) {
      return <WithoutTaskDataComponent solution={solution} />;
    }
    return <WithoutUserDataComponent solution={solution} />;
  };

  return (
    <article>
      <div className="w-full flex justify-between flex-wrap flex-list__container">
        {solutions.map(solution => (
          <div key={solution._id} className="w-1/2 px-2 mb-2 md:w-1/3 lg:w-1/4">
            <SolutionItem key={solution._id} solution={solution} />
          </div>
        ))}
      </div>
    </article>
  );
}

export enum SolutionType {
  WithoutUserData,
  WithoutTaskData,
}

interface SolutionsProps {
  solutions: Solution[];
  type: SolutionType;
}
