export default function taskMapper(task) {
  return {
    content: task.content,
    repo: task.repo,
    images: task.images.map(function (image) {
      return {
        image: image
      };
    }),
    tips: task.tips.map(function (tip) {
      return {
        tip: tip
      };
    }),
    title: task.title,
    tags: task.tags.join(',')
  };
}
