import React, {ReactElement, useRef} from 'react';
import { FieldRenderProps } from 'react-final-form';

export default function MarkdownEditorComponent(
  props: MarkdownEditorProps,
): ReactElement {
  const { input, placeholder, label } = props;
  let textAreaRef = useRef<HTMLTextAreaElement>();
  const inputClassNames =
    'font-mono appearance-none block w-full bg-white text-gray-700 rounded-l py-3 px-4 leading-tight border-transparent border focus:outline-none focus:border-violet_primary';

  const handleAction = (
    action: Actions
  ): React.MouseEventHandler<HTMLButtonElement> => e => {
    e.preventDefault();

    const el = textAreaRef.current;
    if (!el) {
      return;
    }

    const tokens = this.actionToTokens[action];

    this.addTokensToTextarea(el, tokens);
    this.handleTextChange();
  };


  return (
    <div className="mb-3">
      <button type="button" onClick={handleAction('BOLD')}>Bold</button>
      <label
        className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
        htmlFor={input.name}
      >
        {label}
      </label>
      <div className="flex items-stretch">
        <textarea
          placeholder={placeholder}
          {...input}
          className={inputClassNames}
        />
      </div>
    </div>
  );
}

export interface MarkdownEditorProps
  extends FieldRenderProps<string, HTMLElement> {
  label?: string;
  placeholder?: string;
}

export type Actions = "BOLD" | "ITALIC" | "HEADING" | "CODEBLOCK" | "UL" | "OL";
interface Tokens {
  open: string;
  close: string;
}
