import { Solution } from '../../../../lib/types/solution';
import React, { ReactElement } from 'react';
import SolutionBadgeComponent from "../solution-badge/solution-badge.component";



export default function WithoutTaskDataComponent({
  solution,
}: SolutionProps): ReactElement {
  return (
    <section className="w-full h-64 p-2 rounded text-sm shadow hover:shadow-md">
      <SolutionBadgeComponent>{solution.phase}</SolutionBadgeComponent>
      {solution._user.photo && (
        <img
          src={solution._user.photo}
          className="my-2 w-16 h-16 mx-auto rounded-full"
          alt="solution card"
        />
      )}
      <span className="block mb-2 text-sm text-gray-600 w-full text-center">
        {solution._user.login}
      </span>
      <LinkComponent url={solution.repo}>GitHub</LinkComponent>
      <LinkComponent url={solution.demo}>Live</LinkComponent>
    </section>
  );
}

interface SolutionProps {
  solution: Solution;
}
