import axios from 'axios';
import {Solution} from "../../../lib/types/solution";

export default async function addSolution({
  repo,
  demo,
  comment,
  taskId,
}: AddNewSolution): Promise<Solution> {
  return await axios.post('/api/solutions', {
    repo,
    demo,
    comment,
    taskId,
    phase: 'review',
  });
}

interface AddNewSolution extends AddSolutionFormData {
  taskId: string;
}

export interface AddSolutionFormData {
  repo: string;
  demo: string;
  comment: string;
}
